mapper.select:

select ID, `TYPE`, EMAIL, `STATUS`, OUTDATE, FIRST_INSERT, LAST_MODIFIED, CREATE_USER, 
UPDATE_USER, DELETED, DEL_REASON
from TBL_EMAIL_RECORD


		ID = #{id,jdbcType=VARCHAR}


		and EMAIL = #{email,jdbcType=VARCHAR}


		and `TYPE` = #{type,jdbcType=VARCHAR}


		and `STATUS` = #{status,jdbcType=VARCHAR}

	and DELETED = '0'

order by FIRST_INSERT desc
