mapper.select:

select 
from TBL_WALLET_BS_TX_RECORD

	DELETED = '0'

		and FROM_USER = #{fromUser,jdbcType=VARCHAR}


		and TO_USER = #{toUser,jdbcType=VARCHAR}


		and FROM_ADDRESS = #{fromAddress,jdbcType=VARCHAR}


		and TO_ADDRESS = #{toAddress,jdbcType=VARCHAR}


		and CURRENCY_ID = #{currencyId,jdbcType=VARCHAR}


		and STATUS = #{status,jdbcType=VARCHAR}


order by TRAN_TIME desc


select t.*
from TBL_WALLET_BS_TX_RECORD t LEFT JOIN TBL_WALLET_CURRENCY c ON t.CURRENCY_ID = c.ID


		(t.FROM_USER = #{username,jdbcType=VARCHAR} OR t.TO_USER = #{username,jdbcType=VARCHAR})


		and (t.FROM_ADDRESS = #{address,jdbcType=VARCHAR} OR t.TO_ADDRESS = #{address,jdbcType=VARCHAR})


		and t.HASH = #{hash,jdbcType=VARCHAR}


		and t.STATUS = #{status,jdbcType=VARCHAR}


		and t.CURRENCY_ID = #{currencyId,jdbcType=VARCHAR}


		and c.NAME = #{currencyName,jdbcType=VARCHAR}


		and c.SYMBOL = #{currencySymbol,jdbcType=VARCHAR}


order by t.TRAN_TIME desc
