mapper.select:

select 
from TBL_WALLET_CURRENCY


		`NAME` LIKE CONCAT('%',#{name,jdbcType=VARCHAR},'%')


		and SYMBOL LIKE CONCAT('%',#{symbol,jdbcType=VARCHAR},'%')


		 and CONTRACT_ADDRESS = #{contractAddress,jdbcType=VARCHAR}


		and STATUS = #{status,jdbcType=VARCHAR}


		and `TYPE` = #{type,jdbcType=VARCHAR}

		and DELETED='0'

	order by SYMBOL desc

select c.* from TBL_WALLET_CURRENCY c LEFT JOIN TBL_WALLET_ACCOUNT_CURRENCY w ON w.CURRENCY_ID = c.ID LEFT JOIN TBL_WALLET_ACCOUNT a ON w.ACCOUNT_ID = a.ID
a.ID = #{id,jdbcType=VARCHAR}
and a.NAME = #{name, jdbcType=VARCHAR}
and c.DELETED = '0'
order by c.SYMBOL desc
